import { BrowserRouter as Router, Routes, Route, Link, useNavigate } from 'react-router-dom';
import { FaUser, FaShoppingCart, FaSignInAlt, FaSearch } from 'react-icons/fa';
import { useState } from 'react';

export default function App() {
  const [search, setSearch] = useState('');
  const [cartItems, setCartItems] = useState([]);
  const [orderHistory, setOrderHistory] = useState([]);

  const featuredBooks = [
    { id: 1, title: 'Clean Code', author: 'Robert C. Martin', genre: 'Programming', price: 30 },
    { id: 2, title: 'The Pragmatic Programmer', author: 'Andrew Hunt', genre: 'Programming', price: 35 },
  ];

  const comingSoonBooks = [
    { id: 3, title: 'AI for Everyone', author: 'Andrew Ng', genre: 'Technology', price: 40 },
    { id: 4, title: 'Next.js in Action', author: 'Vercel Team', genre: 'Web Development', price: 45 },
  ];

  const handleAddToCart = (book) => {
    setCartItems(prev => {
      const existing = prev.find(item => item.id === book.id);
      if (existing) {
        return prev.map(item => item.id === book.id ? { ...item, quantity: item.quantity + 1 } : item);
      } else {
        return [...prev, { ...book, quantity: 1 }];
      }
    });
  };

  const handleQuantityChange = (id, delta) => {
    setCartItems(prev => prev.map(item => item.id === id ? { ...item, quantity: Math.max(1, item.quantity + delta) } : item));
  };

  const handleCheckout = (card, address) => {
    const order = {
      id: Date.now(),
      items: cartItems,
      card,
      address,
      date: new Date().toLocaleString(),
      total: cartItems.reduce((sum, item) => sum + item.price * item.quantity, 0),
    };
    setOrderHistory(prev => [order, ...prev]);
    setCartItems([]);
    alert("Order placed successfully! A confirmation email has been sent.");
  };

  return (
    <Router>
      <div style={{ fontFamily: 'Arial, sans-serif', background: '#f9f9f9', minHeight: '100vh', padding: '20px' }}>
        <header style={{ background: '#4a90e2', color: '#fff', padding: '10px 20px', borderRadius: '8px', marginBottom: '20px' }}>
          <Link to="/" style={{ textDecoration: 'none', color: 'white' }}>
            <h1>ðŸ“š Online Bookstore System</h1>
          </Link>
          <div style={{ display: 'flex', alignItems: 'center', gap: '15px', marginTop: '10px', flexWrap: 'wrap' }}>
            <Link style={navStyle} to="/login"><FaSignInAlt /> Login</Link>
            <Link style={navStyle} to="/register">Register</Link>
            <Link style={navStyle} to="/profile"><FaUser /> Profile</Link>
            <Link style={navStyle} to="/cart"><FaShoppingCart /> Cart ({cartItems.reduce((sum, item) => sum + item.quantity, 0)})</Link>
            <Link style={navStyle} to="/order-history">Order History</Link>
            <div style={{ display: 'flex', alignItems: 'center', background: '#fff', borderRadius: '5px', padding: '5px 10px' }}>
              <FaSearch color="#4a90e2" />
              <input
                type="text"
                placeholder="Search books..."
                value={search}
                onChange={(e) => setSearch(e.target.value)}
                style={{ border: 'none', outline: 'none', marginLeft: '5px' }}
              />
            </div>
          </div>
        </header>
        <Routes>
          <Route path="/" element={<Home featuredBooks={featuredBooks} comingSoonBooks={comingSoonBooks} handleAddToCart={handleAddToCart} />} />
          <Route path="/register" element={<RegisterPage />} />
          <Route path="/login" element={<LoginPage />} />
          <Route path="/profile" element={<EditProfilePage />} />
          <Route path="/cart" element={<CartPage cartItems={cartItems} handleQuantityChange={handleQuantityChange} />} />
          <Route path="/checkout" element={<CheckoutPage cartItems={cartItems} handleCheckout={handleCheckout} />} />
          <Route path="/order-history" element={<OrderHistoryPage orderHistory={orderHistory} setCartItems={setCartItems} />} />
        </Routes>
      </div>
    </Router>
  );
}

function CheckoutPage({ cartItems, handleCheckout }) {
  const [card, setCard] = useState('1234 5678 9012 3456');
  const [address, setAddress] = useState('123 Main St, City, State, 12345');
  const total = cartItems.reduce((sum, item) => sum + item.price * item.quantity, 0);
  const tax = total * 0.07;
  const grandTotal = total + tax;

  return (
    <div>
      <h2 style={{ color: '#333' }}>Checkout</h2>
      <h3>Order Summary</h3>
      {cartItems.map(item => (
        <div key={item.id}>
          {item.title} - ${item.price} x {item.quantity}
        </div>
      ))}
      <p>Subtotal: ${total.toFixed(2)}</p>
      <p>Tax (7%): ${tax.toFixed(2)}</p>
      <p><strong>Total: ${grandTotal.toFixed(2)}</strong></p>

      <h3>Payment Information</h3>
      <input type="text" value={card} onChange={(e) => setCard(e.target.value)} style={inputStyle} />

      <h3>Shipping Address</h3>
      <input type="text" value={address} onChange={(e) => setAddress(e.target.value)} style={inputStyle} />

      <button style={{ ...buttonStyle, marginTop: '10px' }} onClick={() => handleCheckout(card, address)}>Place Order</button>
    </div>
  );
}

function OrderHistoryPage({ orderHistory, setCartItems }) {
  const handleReorder = (order) => {
    setCartItems(order.items);
    alert("Items added to cart for reorder.");
  };

  return (
    <div>
      <h2 style={{ color: '#333' }}>Order History</h2>
      {orderHistory.length === 0 ? (
        <p>No past orders.</p>
      ) : (
        orderHistory.map(order => (
          <div key={order.id} style={{ background: '#fff', padding: '10px', marginBottom: '10px', borderRadius: '5px' }}>
            <p><strong>Order Date:</strong> {order.date}</p>
            <p><strong>Items:</strong></p>
            {order.items.map(item => (
              <div key={item.id}>{item.title} - ${item.price} x {item.quantity}</div>
            ))}
            <p><strong>Total:</strong> ${order.total.toFixed(2)}</p>
            <button style={buttonStyle} onClick={() => handleReorder(order)}>Reorder</button>
          </div>
        ))
      )}
    </div>
  );
}

// Keep your RegisterPage, LoginPage, EditProfilePage, CartPage, Home, BookGrid, and styling constants (buttonStyle, inputStyle, navStyle) as previously provided.
